mapper.update:
update match set
	mat_stdate = #{mat_stdate}, mat_hour = #{mat_hour}
	where b_num = #{b_num} and m_id = #{m_id}

update match set
 mat_state = 'B'
 where mat_num = #{mat_num}

update match set
 mat_state = 'D'
 where mat_num = #{mat_num}

mapper.select:
select mat_num, m.b_num, mat_hour, mat_stdate, mat_wdate, mat_okdate, mat_state, b_subject
from b_match b, match m
where b.b_num = m.b_num and m.m_id =#{m_id}
